/**
 * Requests jobs
 * Requests response generated by a tool run
 */

import { defineStore } from "pinia";
import Vue from "vue";
import { getAppRoot } from "onload/loadConfig";
import axios from "axios";

export const useJobStore = defineStore("jobStore", {
    state: () => ({
        job: {},
        responses: {},
    }),
    getters: {
        getJob: (state) => {
            return (jobId) => state.job[jobId];
        },
        getResponseForJobId: (state) => {
            return (jobId) => state.responses[jobId];
        },
    },
    actions: {
        async fetchJob(jobId) {
            const { data } = await axios.get(`${getAppRoot()}api/jobs/${jobId}?full=true`);
            this.saveJobForJobId(jobId, data);
        },
        // Setters
        saveJobForJobId(jobId, job) {
            Vue.set(this.job, jobId, job);
        },
        saveResponseForJobId(jobId, response) {
            Vue.set(this.responses, jobId, response);
        },
    },
});
